var PolishCalc={init:function(a){var a=a||[];this.display(this.process(window.prompt("Enter RPN String",a.join(" ")).split(" ").reverse()))},process:function(c){var g=[];var d=0;var a=0;var b=function(){};while(c.length>0){d=c.pop();if(d.match(/^[\*\+\-\/]$/)){switch(String(d)){case"+":b=this.add;break;case"*":b=this.multiply;break;case"/":b=this.divide;break;case"-":b=this.subtract;break}try{g.push(b(g.pop(),g.pop()))}catch(f){alert(f.message)}}else{g.push(d)}}return g},display:function(a){var a=a||[];if(window.confirm("The answer was "+a[a.length-1]+".\n Remaining values on stack: "+a.slice(0,a.length-1)+"\n Input another string?")){this.init(a)}else{window.alert("All done.")}},add:function(d,c){return parseFloat(d)+parseFloat(c)},subtract:function(d,c){return parseFloat(d)-parseFloat(c)},multiply:function(d,c){return parseFloat(d)*parseFloat(c)},divide:function(d,c){return parseFloat(d)/parseFloat(c)}};PolishCalc.init();